package Lec1;

public class Hey_Bye_Okay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 9;
		if (n % 2 == 0 && n % 3 == 0) {
			System.out.println("Hey");
		}
		else if (n % 7 == 0) {
			System.out.println("Bye");
		} 
		else {
			System.out.println("okey");
		}

	}

}
