package Lec1;

public class Math_Opreator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 8;
		int b = 9;
		int c = a + b;
		System.out.println(c);
		c = a - b;
		System.out.println(c);
		int d =a/b;
		System.out.println(d);
		d =a%b;
		System.out.println(d);
		d =a*b;
		System.out.println(d);

	}

}
